<style scoped>
button {
    cursor: pointer;
}

.fill-focused:focus,
.stroke-focused:focus {
  background-color: var(--primary-focus-color) !important;
}

.fill-focused >>> svg path,
.stroke-focused >>> svg path {
  transition: stroke 0.1s;
}

.stroke-focused:focus >>> svg path {
  stroke: var(--primary-color);
}

.fill-focused:focus >>> svg path {
  fill: var(--primary-color);
}

svg path {
  transition: fill 50ms;
}
</style>
